import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroContainer = styled.section`
  min-height: 100vh;
  background: linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%);
  position: relative;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  padding-top: 80px;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    radial-gradient(circle at 20% 80%, rgba(251, 191, 36, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(107, 114, 128, 0.05) 0%, transparent 50%);
  z-index: 1;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  text-align: center;
  position: relative;
  z-index: 2;
`;

const MainHeadline = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 700;
  color: #ffffff;
  margin-bottom: 1.5rem;
  line-height: 1.2;
  text-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
`;

const Tagline = styled(motion.p)`
  font-size: clamp(1.1rem, 2vw, 1.25rem);
  color: #cbd5e1;
  margin-bottom: 3rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.6;
`;

const CTAButton = styled(motion.button)`
  background: linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%);
  color: #0f172a;
  border: none;
  padding: 1rem 2.5rem;
  font-size: 1.1rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 8px 25px rgba(251, 191, 36, 0.3);
  text-transform: uppercase;
  letter-spacing: 1px;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 35px rgba(251, 191, 36, 0.4);
  }
`;

const AnimatedElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1;
  pointer-events: none;
`;

const Crane = styled(motion.div)`
  position: absolute;
  top: 20%;
  right: 10%;
  width: 120px;
  height: 200px;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 8px;
    height: 100%;
    background: linear-gradient(to bottom, #64748b, #475569);
    border-radius: 4px;
  }
  
  &::after {
    content: '';
    position: absolute;
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 6px;
    background: linear-gradient(to right, #64748b, #475569);
    border-radius: 3px;
  }
`;

const CraneHook = styled(motion.div)`
  position: absolute;
  top: 40px;
  left: 50%;
  transform: translateX(-50%);
  width: 4px;
  height: 60px;
  background: #fbbf24;
  border-radius: 2px;
  
  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 20px;
    height: 4px;
    background: #fbbf24;
    border-radius: 2px;
  }
`;

const Building = styled(motion.div)`
  position: absolute;
  bottom: 20%;
  left: 15%;
  width: 80px;
  height: 120px;
  background: linear-gradient(to top, #475569, #64748b);
  border-radius: 4px 4px 0 0;
  
  &::before {
    content: '';
    position: absolute;
    top: 20px;
    left: 10px;
    width: 60px;
    height: 80px;
    background: linear-gradient(to top, #1e293b, #334155);
    border-radius: 2px;
  }
`;

const Blueprint = styled(motion.div)`
  position: absolute;
  top: 30%;
  left: 10%;
  width: 100px;
  height: 80px;
  background: rgba(59, 130, 246, 0.1);
  border: 2px solid rgba(59, 130, 246, 0.3);
  border-radius: 4px;
  
  &::before {
    content: '';
    position: absolute;
    top: 15px;
    left: 15px;
    width: 70px;
    height: 50px;
    background: rgba(59, 130, 246, 0.2);
    border-radius: 2px;
  }
`;

const FloatingParticles = styled(motion.div)`
  position: absolute;
  width: 4px;
  height: 4px;
  background: #fbbf24;
  border-radius: 50%;
`;

const HeroSection = () => {
  const headlineVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.8, ease: "easeOut" }
    }
  };

  const taglineVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.8, delay: 0.2, ease: "easeOut" }
    }
  };

  const buttonVariants = {
    hidden: { opacity: 0, y: 30, scale: 0.9 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { duration: 0.6, delay: 0.4, ease: "easeOut" }
    }
  };

  const craneVariants = {
    hidden: { opacity: 0, x: 100, rotate: -10 },
    visible: { 
      opacity: 1, 
      x: 0, 
      rotate: 0,
      transition: { duration: 1.2, ease: "easeOut" }
    }
  };

  const buildingVariants = {
    hidden: { opacity: 0, y: 100, scale: 0.8 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { duration: 1.2, delay: 0.3, ease: "easeOut" }
    }
  };

  const blueprintVariants = {
    hidden: { opacity: 0, x: -100, rotate: 10 },
    visible: { 
      opacity: 1, 
      x: 0, 
      rotate: 0,
      transition: { duration: 1.2, delay: 0.6, ease: "easeOut" }
    }
  };

  const particleVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: [0, 1, 0], 
      y: [-20, -40, -60],
      transition: { 
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  const hookVariants = {
    hidden: { y: 0 },
    visible: { 
      y: [0, -10, 0],
      transition: { 
        duration: 2,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <HeroContainer id="home">
      <BackgroundPattern />
      
      <AnimatedElements>
        <Crane
          variants={craneVariants}
          initial="hidden"
          animate="visible"
        >
          <CraneHook
            variants={hookVariants}
            initial="hidden"
            animate="visible"
          />
        </Crane>
        
        <Building
          variants={buildingVariants}
          initial="hidden"
          animate="visible"
        />
        
        <Blueprint
          variants={blueprintVariants}
          initial="hidden"
          animate="visible"
        />
        
        {[...Array(8)].map((_, i) => (
          <FloatingParticles
            key={i}
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 2}s`
            }}
            variants={particleVariants}
            initial="hidden"
            animate="visible"
          />
        ))}
      </AnimatedElements>

      <ContentWrapper>
        <MainHeadline
          variants={headlineVariants}
          initial="hidden"
          animate="visible"
        >
          Building the Future with
          <br />
          <span style={{ color: '#fbbf24' }}>Strength and Precision</span>
        </MainHeadline>
        
        <Tagline
          variants={taglineVariants}
          initial="hidden"
          animate="visible"
        >
          We transform architectural visions into reality, delivering exceptional 
          construction projects that stand the test of time and exceed expectations.
        </Tagline>
        
        <CTAButton
          variants={buttonVariants}
          initial="hidden"
          animate="visible"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          Get a Quote
        </CTAButton>
      </ContentWrapper>
    </HeroContainer>
  );
};

export default HeroSection;
