import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroContainer = styled.section`
  min-height: 100vh;
  position: relative;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  padding-top: 80px;
`;

const VideoBackground = styled.video`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: 1;
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.6);
  z-index: 2;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  text-align: center;
  position: relative;
  z-index: 3;
`;

const MainHeadline = styled(motion.h1)`
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 700;
  color: #ffffff;
  margin-bottom: 1.5rem;
  line-height: 1.2;
  text-shadow: 0 4px 8px rgba(0, 0, 0, 0.5);
`;

const Tagline = styled(motion.p)`
  font-size: clamp(1.1rem, 2vw, 1.25rem);
  color: #ffffff;
  margin-bottom: 3rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.6;
`;

const CTAButton = styled(motion.button)`
  background: rgba(255, 255, 255, 0.2);
  color: #ffffff;
  border: 2px solid rgba(255, 255, 255, 0.3);
  padding: 1rem 2.5rem;
  font-size: 1.1rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
  text-transform: uppercase;
  letter-spacing: 1px;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    border-color: rgba(255, 255, 255, 0.5);
    transform: translateY(-2px);
  }
`;

const CTAButtonSmall = styled.div`
  font-size: 0.9rem;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 0.5rem;
  text-transform: none;
  letter-spacing: normal;
`;

const HeroSection = () => {
  const headlineVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.8, ease: "easeOut" }
    }
  };

  const taglineVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.8, delay: 0.2, ease: "easeOut" }
    }
  };

  const buttonVariants = {
    hidden: { opacity: 0, y: 30, scale: 0.9 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { duration: 0.6, delay: 0.4, ease: "easeOut" }
    }
  };

  return (
    <HeroContainer id="home">
      <VideoBackground autoPlay muted loop playsInline>
        <source src="/Hero_Section_video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </VideoBackground>
      
      <Overlay />
      
      <ContentWrapper>
        <MainHeadline
          variants={headlineVariants}
          initial="hidden"
          animate="visible"
        >
          Building the Future with
          <br />
          <span style={{ color: '#fbbf24' }}>Strength and Precision</span>
        </MainHeadline>
        
        <Tagline
          variants={taglineVariants}
          initial="hidden"
          animate="visible"
        >
          We transform architectural visions into reality, delivering exceptional 
          construction projects that stand the test of time and exceed expectations.
        </Tagline>
        
        <motion.div
          variants={buttonVariants}
          initial="hidden"
          animate="visible"
        >
          <CTAButtonSmall>Get a Quote</CTAButtonSmall>
          <CTAButton
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Get Quotation
          </CTAButton>
        </motion.div>
      </ContentWrapper>
    </HeroContainer>
  );
};

export default HeroSection;
